import { createCanvas } from 'canvas';
import { ITask } from 'typing';
// const fs = require('fs');
const { JSDOM } = require('jsdom');
const echarts = require('echarts');

// 处理接口返回的数据
const handleData = (chartList) => {
    let legendSelected = {};
    // 默认选择靠前的三个版本进行展示
    const selectedVersionNames = Object.keys(legendSelected).filter((item) => legendSelected[item]);
    const versionNames = Array.from(new Set(chartList?.map((task: ITask) => task.versionName)));
    const legendSelectedTemp = {};
    if (!selectedVersionNames.length) {
        versionNames.forEach((name: any, idx: number) => {
            legendSelectedTemp[name] = idx < 3;
        });
        legendSelected = legendSelectedTemp;
    }

    if (chartList?.length) {
        const selectedVersionNames = Object.keys(legendSelected).filter(
            (item) => legendSelected[item]
        );
        const versionIds = Array.from(new Set(chartList?.map((task: ITask) => task.versionId)));

        const series = versionIds.map((versionId: any) => {
            return {
                name:
                    chartList.find((task: ITask) => task.versionId === versionId)?.versionName ||
                    '',
                type: 'line',
                data: chartList
                    .filter((task: ITask) => task.versionId === versionId)
                    .map((task: ITask) => task.score),
                smooth: true,
                taskIds:
                    chartList
                        .filter((task: ITask) => task.versionId === versionId)
                        .map((task: ITask) => task.taskId) || [],
            };
        });

        const maxLength = Math.max.apply(
            null,
            series
                .filter((item) => selectedVersionNames.includes(item.name))
                .map((item) => item.data.length)
        );
        const xAxisData = [];
        for (let i = 0; i < maxLength; i++) {
            xAxisData.push(`第${i + 1}次`);
        }

        return { versionNames, legendSelected, xAxisData, series };
    }
};

// 生成 echarts option
export const getOptions = (chartList) => {
    const {
        versionNames,
        legendSelected,
        xAxisData = [],
        series = [],
    } = handleData(chartList) || {};
    const yMin = Math.min.apply(null, series.map((item) => item.data).flat(Infinity));
    const option = {
        tooltip: {
            trigger: 'axis',
            formatter: function (params: any) {
                let relVal = params[0].name;
                params.forEach((param: any) => {
                    const value = series[param.seriesIndex].data[param.dataIndex];
                    const taskId = series[param.seriesIndex].taskIds[param.dataIndex];
                    const seriesName = param.seriesName;
                    relVal +=
                        '<br/>' +
                        param.marker +
                        `[${taskId}]${seriesName}` +
                        `<span style='font-weight:500;margin-left: 10px'>${value}分</span>`;
                });

                return relVal;
            },
        },
        // echarts 顶部选择展示的版本
        legend: {
            data: versionNames,
            selected: legendSelected,
        },
        grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true,
        },
        xAxis: {
            type: 'category',
            boundaryGap: false,
            data: xAxisData,
        },
        yAxis: {
            type: 'value',
            min: yMin > 20 ? 20 : 0,
        },
        series,
        devicePixelRatio: 2,
        backgroundColor: '#fff',
    };

    return option;
};

// 渲染图表
export const renderChart = (arr = []) => {
    const config = {
        width: 950,
        height: 400,
    };
    const list = [];
    arr.forEach(({ chartList, projectId, name }) => {
        const option = getOptions(chartList);

        const canvas = createCanvas(config.width, config.height);
        const chart = echarts.init(canvas as any);
        chart.setOption(option);
        const base64 = canvas.toDataURL('image/png');
        chart.dispose();
        list.push({ projectId, name, base64 });
    });
    const html = renderHtml(list);
    return html;
};

const renderHtml = (list) => {
    // 创建一个虚拟的DOM环境
    const dom = new JSDOM(
        `
            <!DOCTYPE html>
            <html>
                <head>
                    <meta charset="utf-8" />
                    <title>易测性能检测平台 - 数据周报</title>
                </head>
                <body>
                    <div style="width: 400px; background-color: #f2f7fa; padding: 30px 0">
                        <div style="width: 360px; margin: 0 auto">
                            <!-- 背景图 -->
                            <img
                                style="width: 360px; border-radius: 6px"
                                src="data:image/jpeg;base64,"
                            />

                            <!-- 打招呼 -->
                            <div
                                style="
                                    border-radius: 6px;
                                    background-color: #fff;
                                    margin: 16px 0;
                                    padding: 20px;
                                "
                            >
                                <div style="font-weight: 600">嗨，</div>
                                <div
                                    style="
                                        padding-top: 10px;
                                        font-size: 14px;
                                        text-indent: 2em;
                                        line-height: 24px;
                                    "
                                >
                                    这是一份易测提供的性能测试评分周报，分享给你，你也可以在易测查看完整数据。
                                </div>
                            </div>

                            ${renderChartItem(list)}
                        </div>
                    </div>
                </body>
            </html>
        `
    );

    const renderedHTML = dom.serialize();

    // 将渲染后的HTML保存到文件，用于开发时调试效果
    // fs.writeFileSync('static/chart.html', renderedHTML);

    return renderedHTML;
};

const renderChartItem = (list = []) => {
    let html = '';
    list.forEach(({ projectId, name, base64 }) => {
        html += `<!-- 性能趋势图 -->
        <div style="border-radius: 6px; background-color: #fff; margin: 16px 0">
            <div style="padding: 20px 20px 0; font-weight: 600">${name} 近七天</div>
            <div
                style="
                    padding: 10px 20px 20px;
                    font-size: 14px;
                    text-indent: 2em;
                    line-height: 24px;
                "
            >
                下方的折线图是过去七天子产品的性能趋势图，你可以通过观察趋势图来判断是否有异常数据，当你发现分数异常变化时，请前往易测查看详细数据。
            </div>
            <!-- 性能趋势折线图 -->
            <img style="width: 340px; padding: 0 10px 20px" src="${base64}" />
            <div
                style="
                    padding: 0 20px 10px;
                    font-size: 12px;
                    text-indent: 2em;
                    line-height: 24px;
                    text-align: right;
                "
            >
                <a
                    href="http://yice.dtstack.cn/redirect?projectId=${projectId}&type=chart"
                    style="color: #1d78ff"
                    >在易测中查看更多</a
                >
            </div>
        </div>`;
    });
    return html;
};
